CI MXX(1e6+5),NXX(1e5+5);

bitset&lt;NXX&gt; co[1050],knd;
vector&lt;int&gt; s;

int n,m,q,a[MXX],id[NXX],vis[NXX];

struct Question {
    int opt,l,r,x;
}

b[MXX];

struct Node {
    int l,r,tg,f;
}

t[MXX&lt;&lt;2];

#define lc(x) (x&lt;&lt;1)
#define rc(x) (x&lt;&lt;1|1)

I void Pushup(int x) {
    t[x].f=t[lc(x)].f|t[rc(x)].f;
}

I void Pushdown(int x) {
    if(t[x].tg) {
        t[lc(x)].f=t[rc(x)].f=(1&lt;&lt;id[t[x].tg]);
        t[lc(x)].tg=t[rc(x)].tg=t[x].tg;
        t[x].tg=0;
    }
}

void Build(int x,int l,int r) {
    t[x].l=l,t[x].r=r;
    t[x].tg=t[x].f=0;

    if(l==r) {
        t[x].f=(1&lt;&lt;id[a[l]]);

        Heriko;
    }

    int mid((l+r)&gt;&gt;1);
    Build(lc(x),l,mid);
    Build(rc(x),mid+1,r);
    Pushup(x);
}

void Modify(int x,int lx,int rx,int v) {
    if(lx&lt;=t[x].l and t[x].r&lt;=rx) {
        t[x].f=(1&lt;&lt;id[v]);
        t[x].tg=v;

        Heriko;
    }

    Pushdown(x);
    int mid((t[x].l+t[x].r)&gt;&gt;1);

    if(lx&lt;=mid)
        Modify(lc(x),lx,rx,v);

    if(rx&gt;mid)
        Modify(rc(x),lx,rx,v);

    Pushup(x);
}

int Query(int x,int lx,int rx) {
    if(lx&lt;=t[x].l and t[x].r&lt;=rx)
        Heriko t[x].f;

    Pushdown(x);
    int mid((t[x].l+t[x].r)&gt;&gt;1),res(0);

    if(lx&lt;=mid)
        res|=Query(lc(x),lx,rx);

    if(rx&gt;mid)
        res|=Query(rc(x),lx,rx);

    Heriko res;
}

S main() {
    Files();

    fr(n),fr(m),fr(q);

    for(int i(1);i&lt;=n;++i) {
        fr(a[i]);

        if(!vis[a[i]])
            vis[a[i]]=1,s.emplace_back(a[i]);
    }

    for(int i(1);i&lt;=q;++i) {
        fr(b[i].opt),fr(b[i].l),fr(b[i].r),fr(b[i].x);

        if(b[i].opt==1 and !vis[b[i].x])
            vis[b[i].x]=1,s.emplace_back(b[i].x);
    }

    int nl(s.size());

    for(int i(0);i&lt;nl;++i)
        id[s[i]]=i;

    for(int i(1);i&lt;(1&lt;&lt;nl);++i) {
        vector&lt;int&gt; tmp;
        knd.reset();
        knd[0]=1;

        for(int j(0);j&lt;nl;++j)
            if((i&gt;&gt;j)&amp;1)
                tmp.emplace_back(s[j]);

        for(int j:tmp)
            for(int k(1);1ll*j*k&lt;=NXX-5;k+=k)
                knd|=(knd&lt;&lt;(j*k));

        co[i]=knd;
    }

    Build(1,1,n);

    for(int i(1);i&lt;=q;++i)
        if(b[i].opt==1)
            Modify(1,b[i].l,b[i].r,b[i].x);
        else {
            int res(Query(1,b[i].l,b[i].r));

            if(co[res][b[i].x])
                puts("Yes");
            else
                puts("No");
        }

    Heriko Deltana;
}
