CI MXX(1e6+25),N(5e5);

bitset&lt;MXX&gt; f;

int n,a[MXX];

S main() {
    Files();

    fr(n);

    if(n&gt;1000) {
        puts("Yes");

        Heriko Deltana;
    }

    for(int i(1);i&lt;=n;++i)
        fr(a[i]);

    f[a[1]+N]=1;

    for(int i(2);i&lt;=n;++i) {
        f|=(f&lt;&lt;a[i]),f|=(f&gt;&gt;a[i]);

        if(f[N]) {
            puts("Yes");

            Heriko Deltana;
