const ll MOD = 1e9 + 7;

template &lt;typename T&gt; inline void read(T &amp;x) {
    x = 0; int f = 1; char c = getchar();
    for (; !isdigit(c); c = getchar()) if (c == '-') f = -f;
    for (; isdigit(c); c = getchar()) x = x * 10 + c - '0';
    x *= f;
}

ll n, m, ans, t;

inline ll fpow(ll a, int b) {
    ll res = 1;
    while (b) {
        if (b &amp; 1) (res *= a) %= MOD;
        b &gt;&gt;= 1, a = a * a % MOD;
    }
    return res;
}

inline void MAIN() {
    read(n), read(m);
    if (n == 1) ans = m;
    else if (n == 2) ans = m * (m - 1) % MOD;
    else ans = m * (m - 1) % MOD * (m - 2) % MOD;
    for (int i = 4; i &lt;= n; ++ i)
      ans = (m * fpow(m - 1, i - 1) % MOD - ans + MOD) % MOD;
    printf("%lld\n", ans);
}

int main() {
#ifdef LOCAL
    freopen("in.txt", "r", stdin);
    freopen("out.txt", "w", stdout);
#endif
    read(t); while (t --) MAIN(); return 0;
}

