                (f[l][r]+=1)%=MOD;

                continue;
            }

            /*----F----*/

            if(r-l-1&lt;=k and co[l+1][r-1])
                (f[l][r]+=1)%=MOD;

            (f[l][r]+=f[l+1][r-1]+g[l+1][r-1])%=MOD;

            for(int i(l+1);i&lt;=Hmin(l+k,r-2);++i)
                if(co[l+1][i])
                    (f[l][r]+=f[i+1][r-1]+g[i+1][r-1])%=MOD;

            for(int i(r-1);i&gt;=Hmax(l+2,r-k);--i)
                if(co[i][r-1])
                    (f[l][r]+=f[l+1][i-1]+g[l+1][i-1])%=MOD;

