            tmp=0;

            for(int i(l+2);i&lt;=nex[l+1];++i)
                (tmp+=f[i][r])%=MOD;

            (g[l][r]+=((f[l][l+1]+g[l][l+1])%MOD*tmp))%=MOD;

            for(int i(l+2);i&lt;r-1;++i)
