        }

        if(l.size() and r.size() and l.back()==r.back())
        {
            flg=0;
            ans[++cnt]='R',ans[m-cnt+1]='L';
            l.pop_back();
            r.pop_back();

            continue;
        }

        if(r.size()&gt;1 and r.front()==r.back())
        {
            flg=0;
            ans[++cnt]='R',ans[m-cnt+1]='R';
            r.pop_front();
            r.pop_back();

            continue;
        }
    }

    for(int i(1);i&lt;=m;++i)
        putchar(ans[i]);

    puts("");
}

