CI MXX(2e5+1);

const double INF(1e9);

int n,k,a[MXX],mx;

double ans,b[MXX],f[MXX];

I bool Check(double x)
{
    for(int i(1);i&lt;=n;++i)
        b[i]=b[i-1]+a[i]-x;

    int hd(0);
    f[hd]=INF;

    for(int i(1);i&lt;=n;++i)
    {
        if(b[i]&lt;0.0 or b[i]&gt;b[n])
            continue;

        int pos(lower_bound(f,f+1+hd,b[i])-f);
        f[pos]=b[i];

        if(pos==hd)
            f[++hd]=INF;
    }

    Heriko hd&gt;=k;
}

S main()
{
    Files();

    fr(n),fr(k);

    for(int i(1);i&lt;=n;++i)
        fr(a[i]),mx=Hmax(mx,a[i]);

    double l(0),r(mx*1.0);

    while(l+(1e-4)&lt;r)
    {
        double mid((l+r)/2.0);

        if(Check(mid))
            l=mid,ans=mid;
        else
            r=mid;
    }

    printf("%.4lf",ans);

    Heriko Deltana;
}
